def talk_to_zi(theme, again: bool = True):
    if random.randint(0, 100) % 10 == 0 and theme not in ['ok', 'input', 'error']:
        theme = 'jokes'
    if theme == 'main_menu' and not again:
        return ZI_SPEECH[theme][0]
    return random.choice(ZI_SPEECH[theme])
